mapper.select:
select * from (
 select rownum rnum, B.* from(
	select * from board
	order by board_no desc
 ) B
 order by rnum
 ) R
 where rnum between #{startNo} and #{endNo}

mapper.update:
update board
set hit = hit+1
where board_no = #{board_no}

update board
set title = #{title}, content = #{content}
where board_no = #{board_no}
